int remover(tlista *L, int pos, telem *elem){ /* O parâmetro elem irá receber o elemento encontrado */ /* Retorna 0 se a posição for inválida. Caso contrário, retorna 1 */ tlista a, p; int n; if (vazia(*L)) return 0; /* erro: lista vazia */ p = *L; n = 1; while ((n<=pos-1) && (p!=NULL)) { a = p; p = p->prox; n++; } if (p == NULL) return 0; /* erro: posição inválida */ *elem = p->dado; if (pos == 1) *L = p->prox; else a->prox = p->prox; free(p); return(1);}
